<?phpnamespace App\Models;use Illuminate\Database\Eloquent\Factories\HasFactory;use Illuminate\Database\Eloquent\Model;use Illuminate\Database\Eloquent\SoftDeletes;class Customer extends Model{    use SoftDeletes, HasFactory;    protected $fillable = [        'name',        'email',        'phone',        'sent_count'    ];    protected $casts = [        'sent_count' => 'integer'    ];}