<?phpnamespace App\Models;use Illuminate\Database\Eloquent\Model;use Illuminate\Database\Eloquent\SoftDeletes;class Smtp extends Model{    use SoftDeletes;    protected $fillable = [        'host',        'port',        'encryption',        'username',        'password',        'active',        'sending_quota',        'sending_concurrent',        'sent_count',    ];    protected $casts = [        'active' => 'boolean',        'sending_quota' => 'integer',        'sending_concurrent' => 'integer',        'sent_count' => 'integer',    ];}