<?phpnamespace App\Nova\Resources\Campaign;use App\Models\Campaign\Sender;use App\Nova\Resource;use Illuminate\Http\Request;use Laravel\Nova\Fields\ID;use Laravel\Nova\Fields\Number;use Laravel\Nova\Fields\Text;class SenderResource extends Resource{    public static string $model = Sender::class;    public static $title = 'name';    public static $search = [        'name'    ];    public static $group = 'Campaign';    public function fields(Request $request): array    {        return [            ID::make()                ->hide()                ->sortable(),            Text::make('Name')                ->sortable()                ->rules('required'),            Text::make('From Mail', 'mail')                ->hide()                ->sortable()                ->rules('nullable'),            Number::make('Sent Count')                ->hideWhenCreating()                ->default(0)                ->sortable()                ->rules('integer'),        ];    }    public function cards(Request $request): array    {        return [];    }    public function filters(Request $request): array    {        return [];    }    public function lenses(Request $request): array    {        return [];    }    public function actions(Request $request): array    {        return [];    }    public static function label(): string    {        return 'Sender';    }}