<?phpnamespace App\Nova\Resources\Campaign;use App\Models\Campaign\Subject;use App\Nova\Resource;use Illuminate\Http\Request;use Laravel\Nova\Fields\ID;use Laravel\Nova\Fields\Number;use Laravel\Nova\Fields\Text;class SubjectResource extends Resource{    public static string $model = Subject::class;    public static $title = 'id';    public static $search = [        'id', 'value'    ];    public static $group = 'Campaign';    public function fields(Request $request): array    {        return [            ID::make()                ->hide()                ->sortable(),            Text::make('Subject', 'value')                ->copyable()                ->sortable()                ->rules('required')                ->help("<pre>".Resource::hintsText()."</pre>"),            Number::make('Sent Count')                ->hideWhenCreating()                ->default(0)                ->sortable()                ->rules('integer'),        ];    }    public function cards(Request $request): array    {        return [];    }    public function filters(Request $request): array    {        return [];    }    public function lenses(Request $request): array    {        return [];    }    public function actions(Request $request): array    {        return [];    }    public static function label(): string    {        return 'Subject';    }}