<?phpnamespace Database\Seeders;use Illuminate\Database\Seeder;class CampaignSeeder extends Seeder{    public function run(): void    {        $contents = \App\Models\Campaign\Content::factory()            ->count(10)            ->create();        $subjects = [            'Urgent: Your account has been locked! (Ref: {{ $uuid }})',            'Attention Required: Your account has been temporarily suspended (Ref: {{ $uuid }})',            'Important Security Notice: Your account has been locked for suspicious activity (Ref: {{ $uuid }})',            'Action Required: Your account has been locked due to a possible security breach (Ref: {{ $uuid }})',            'Alert: Your account has been locked for unauthorized access attempts (Ref: {{ $uuid }})',        ];        foreach ($subjects as $subject) {            \App\Models\Campaign\Subject::query()->create([                'value' => $subject,            ]);        }    }}